import { call, put, takeLatest } from 'redux-saga/effects';
import { types as mapTypes } from '../reducers/map';
import {getSupportById} from '../../utils/JSAPI';


//WORKER

// WORKER //
function* setSelectSupport (action) {
    try {
        // call API to fetch config
        const config = yield call(getAppConfig);

        // Put config in store
        yield put({
            type: configTypes.SET_CONFIG,
            payload: config
        });

    } catch (e) {
        console.log('SAGA ERROR: config/fetchConfig, ', e);
    }
}


// WATCHER //
export function* watchLayers() {
    yield takeLatest(mapTypes.MAP_CLICKED, setSelectSupport);
}
