import React, {Component} from 'react'
import {SignType, addOptionsToSelect} from '../../../SignworksJSON';
import './Zone.css'
const amp = /[&]/;

export default class Zone extends Component {
    constructor(props) {
        super(props)
        const zone = this.zoneParse(props.zone)

        this.state = {

            ward1: zone[0],
            anc1: zone[1],
            ward2: zone[2],
            anc2: zone[3]

        }

    }

    signTypes = new SignType();

    selectZoneHandler = (evt) => {}

    zoneParse = (zoneValue) => {
        // why don't we just set the controls directly?  Because they don't exist yet.
        // we don't have to check for all possible garbage because the zoneValue has
        // already passed ZoneVerify Don't run this without running and passing
        // zoneVerify first!
        let zoneArray = [];
        if (zoneValue) {
            // set first cell
            zoneArray[0] = zoneValue[0]
        } else {
            //empty zone id
            return "";
        }
        // set second cell
        if (!zoneValue[1]) {
            //just one ward no anc
            return zoneArray;
        } else if (amp.test(zoneValue[1])) {
            //second digit is amp so third must be ward
            zoneArray[2] = zoneValue[2];
            if (!zoneValue[3]) {
                // ward + amp + ward and done
                return zoneArray;
            } else {
                //ward + amp + ward + anc and done
                zoneArray[3] = zoneValue[3];
                return zoneArray;
            }
        } else {
            //ward + anc + ...
            zoneArray[1] = zoneValue[1];
            if (amp.test(zoneValue[2])) {
                zoneArray[2] = zoneValue[3];
                if (!zoneValue[4]) {
                    // ward + anc + amp + ward and done
                    return zoneArray;
                } else {
                    // ward + anc + amp + ward + anc
                    zoneArray[3] = zoneValue[4];
                    return zoneArray;
                }
            } else {
                // ward + anc and done
                return zoneArray;
            }
        }

    }
    render() {
        return (
            <span>
                Zone:<select value={this.state.ward1} onChange={this.selectZoneHandler}>
                {addOptionsToSelect(this.signTypes._codedValuesWards)}</select>
                <select></select>
                <select></select>
                <select></select>
            </span>
        )
    }
}
