import { loadModules } from 'esri-loader';



const err = (e) =>{ alert ('an error occurred in JSAPI  ' + e.message)}




export function  getSupportById (args) {
  const id = args[0];
  const layer = args[1];
   
    loadModules([        "esri/tasks/support/Query"])
    .then(([Query]) => {
        let query = new Query();
        query.where = "OBJECTID = " + id;
        query.outFields = ["*"];
        query.returnGeometry = true;
      
        layer.queryFeatures(query).then(
            function (results) {
              
                if (results.features.length > 0) {
                   
                   return results.features[0];
                } else {
                    alert('support was not retrieved')
                }
            },
            err);

     
     
    })
    .catch(err => {
      // handle any errors
      console.error(err);
    });
}
 
export function getSupportByExtent(args) {
    const extent = args[0];
    console.log('extent is JSAPI',extent)
    const supportLayer = args[1];
   // console.log(supportLayer);

    loadModules([
"esri/tasks/support/Query"
    ])
    .then( ([
Query
    ]) => {

        let query = new Query();
        query.geometry = extent;
        query.outFields = ["*"];
        query.returnGeometry = true;
    
        supportLayer.queryFeatures(query).then(
            function (results) {
                if (results.features.length > 0) {
                    alert('alleged results')
                }
            },
            err);
    });
   

}


export function pointToExtent  ( view, point, toleranceInPixel ) {
    loadModules([
        "esri/geometry/Extent"
    ])
    .then( ([
Extent
    ]) => {
 
        //calculate map coords represented per pixel
        let pixelWidth = view.extent.width / view.width;
        
        //calculate map coords for tolerance in pixel
        let toleraceInMapCoords = toleranceInPixel * pixelWidth;
        //calculate & return computed extent
        const returnExtent = new Extent(point.x - toleraceInMapCoords,
            point.y - toleraceInMapCoords,
            point.x + toleraceInMapCoords,
            point.y + toleraceInMapCoords,
            view.spatialReference);
            console.log ('pointToExten',returnExtent)
        return returnExtent;
    });

}
