import React, {Component} from 'react'
import styled from 'styled-components';
import Support from './Support/Support'
import {connect} from 'react-redux';
import {bindActionCreators} from 'redux';
import {actions as mapActions} from '../../redux/reducers/map';
import {actions as graphicActions} from '../../redux/reducers/graphic'
import Signs from './Signs/Signs';
import SignCreator from './Signs/SignCreator';

const RightDiv = styled.div ` 


 width:30%;
 
`
class RightBar extends Component {

    handleModalClicked = (evt, type, index) => {

        this
            .props
            .modalClicked(true, type, index)
    }

    addSignHandler = (evt) => {
console.log('props', this.props)
      
       let newSign =  { "attributes": {
            "SIGNCODE": "unclassified",
            "SIGNSIZE": null,
            "SIGNTEXT": null,
            "MUTCD": null,
            "SIGNFACING": null,
            "SIGNNUMBER": null,
            "SIGNARROWDIRECTION": null,
            "ORIGIN_ID": null,
            "MANUAL_SEGID": null,
            "SIGNORDER": null,
            "SIGNSTATUS": 1,
            "ZONE_ID": null,
            "SUPPORTID": this.props.map.support.attributes.GLOBALID
           }}
         
        //save new sign
        this.props.newSign(this.props.support, newSign, this.props.config.featureURLs)
       

    }

    render() {

        return (

            <RightDiv >

                <Support sel={this.props.map.support} editClick={this.handleModalClicked}></Support>
                <Signs signs={this.props.map.signs} editClick={this.handleModalClicked}></Signs>
                <SignCreator sel={this.props.map.support} click={this.addSignHandler}></SignCreator>
            </RightDiv>
        )
    }
}

const mapStateToProps = state => ({map: state.map, graphic: state.graphic});

const mapDispatchToProps = function (dispatch) {
    return bindActionCreators({
        ...mapActions,
        ...graphicActions
    }, dispatch);
}

export default connect(mapStateToProps, mapDispatchToProps)(RightBar);
