import {call, takeLatest} from 'redux-saga/effects';
import {types as mapTypes} from '../reducers/map';
import {getFullSignPost} from './reload'
import {saveSupport} from '../../utils/JSAPI';

// WORKER //

function * addNewSupport(action) {
    try{

        console.log('action.payload.support :', action.payload.support);
   
        //build a new , blank support to save
        let newSupport = {};
        newSupport.attributes = {
            ANGLE: null,
BASETYPE: null,
COMMENTS: null,
CREATED_DATE: null,
CREATED_USER: null,
FROMDATE: null,
GLOBALID: null,
LAST_EDITED_DATE: null,
LAST_EDITED_USER: null,
LOCATION: null,
MEASURE: null,
MSENDDATE: null,
MSSTARTDATE: null,
MSUTILITYID: null,
NUMBEROFBASES: null,
OBJECTID: null,
ORIGIN_ID: null,
ROUTEID: null,
ROUTEID_ALT: null,
SEG_DIR: null,
SIDE: null,
STREETSEGID: null,
STREETSEGID_ALT: null,
SUBBASE: null,
SUPPORTHEIGHT: null,
SUPPORTSTATUS: 1,
SUPPORTTYPE: 14,
TODATE: null,
Z: null
        }

      
       //  yield call(saveSupport, [action.payload.support, true, action.payload.layers.supports]);
        
       //  yield getFullSignPost(action);
    }
    
    catch (e) {
        console.log('SAGA ERROR: map/saveSelectedSupport, ', e);
    }
}

// WATCHER //
export function * watchLayers() {
   
    yield takeLatest(mapTypes.NEW_SUPPORT_S, addNewSupport);
}
