import React from 'react'
import './ModalWrapper.css'

export default function ModalWrapper(props) {

    const handleBackgroundClick = (e) => {
        if (e.target === e.currentTarget) 
            props.hideModal();
        }
    
    const onOk = () => {
        alert('ok')
      //  props.onOk();
        props.modalClicked(false, null);
    }
    console.log('props', props.map)
    const okButton = props.map.showOk
        ? (
            <button onClick={onOk} disabled={props.okDisabled}>
                OK
            </button>
        )
        : null;

    return (
        <div className="ModalWrapper" onClick={handleBackgroundClick}>
            <header>
                <h1>{props.title}</h1>

                <button onClick={props.hideModal}>Close</button>
            </header>

            {props.children}

            {okButton}
        </div>
    )
}
