import React, {Component} from 'react'
import {connect} from 'react-redux';
import {bindActionCreators} from 'redux';
import {actions as mapActions} from '../../redux/reducers/map';
import {actions as graphicActions} from '../../redux/reducers/graphic'
import {mapModes} from '../../redux/reducers/graphic'
import './Banner.css'
import UserAccount from '../UserAccount';
import styled, {css} from 'styled-components';
import logo from "../../img/logo.png"

class Banner extends Component {
    signIn = () => {
        this
            .props
            .checkAuth("https://dcgis.maps.arcgis.com");
    }

    signOut = () => {
        this
            .props
            .logout(this.props.config.portalUrl);
    }

    bannerToolHandler = (evt) => {

        switch (evt.target.value) {
            case '0':
                return;
            case '1':

                this
                    .props
                    .setMapClickMode(mapModes.SELECT_SUPPORT_MODE, 'default');
                evt.target.value = '0';
                break;

            case "2": //SuperQuery™
                this
                    .props
                    .setMapClickMode(mapModes.SELECT_SUPPORT_MODE, 'default');
                this
                    .props
                    .modalClicked(true, "QUERY", null);
                evt.target.value = '0';
                break;

            case "3":
                this
                    .props
                    .setMapClickMode(mapModes.ADD_SUPPORT_MODE, 'crosshair');
                evt.target.value = '0';
                break;
            case "4":
                this
                    .props
                    .modalClicked(true, "MAR", null);
                evt.target.value = '0';

            default:
                //do nothing

                return null;
        }
    }

    bannerActionHandler = (evt) => {
        switch (evt.target.value) {
            case '1':
                this
                    .props
                    .removeQueryGraphics();

                evt.target.value = "0"

                break;
            case '2':
                this
                    .props
                    .showRetiredPosts(1);
                evt.target.value = '0';
                break;
            case '3':
                this
                    .props
                    .showRetiredPosts(2);
                evt.target.value = '0';
                break;
            case '4':
                this
                    .props
                    .showRetiredPosts(0);
                evt.target.value = '0';
                break;
            case '5':
                this
                    .props
                    .showRetiredPosts(10000);
                evt.target.value = '0';
                break;
            case '6':
                this
                    .props
                    .showRetiredPosts();
                evt.target.value = '3';
                break;

            default:
                return null;
        }
    }

    render() {
        return (
            <div className="Banner">
                <img src={logo} className="AppLogo" alt="logo"/>

                <div className="AppTools">
                    <span>
                        <select onChange={this.bannerToolHandler} className="ActionSelect">
                            <option value={0}>...Tools</option>
                            <option value={1}>Select Support</option>
                            <option value={2}>Sign Query</option>
                            {this.props.auth.isEditor
                                ? <option value={3} disabled={this.props.graphic.leftVisible}>Add Support</option>
                                : null}
                            <option value={4}>MAR Query</option>

                        </select>

                    </span>
                    <span>
                        <select onChange={this.bannerActionHandler} className="ActionSelect">
                            <option value={0}>...Actions</option>
                            <option value={1}>Clear Query Graphics</option>
                            <option value={2}>Show Retired Supports</option>
                            <option value={3}>Show Only Retired Supports</option>
                            <option value={4}>Hide Retired Supports</option>
                            <option value={5}>Show Only Active Supports</option>
                            <option value={6}>Hide All Supports</option>
                        </select>
                    </span>

                </div>

                <UserAccount
                    className="UserAccount"
                    user={this.props.auth.user}
                    portal={this.props.auth.user
                    ? this.props.auth.user.portal
                    : null}
                    loggedIn={this.props.auth.loggedIn}
                    signIn={this.signIn}
                    signOut={this.signOut}/>

            </div>
        )
    }
}

const mapStateToProps = state => ({map: state.map, graphic: state.graphic, auth: state.auth, config: state.config});

const mapDispatchToProps = function (dispatch) {
    return bindActionCreators({
        ...mapActions,
        ...graphicActions
    }, dispatch);
}

export default connect(mapStateToProps, mapDispatchToProps)(Banner);
