import { loadModules } from 'esri-loader';



// const err = () =>{ alert ('an error occurred in JSAPI')}

export function  getSupportById (id, layer) {
   
    loadModules([        "esri/tasks/support/Query"])
    .then(([Query]) => {
        let query = new Query();
        query.where = "OBJECTID = " + id;
        query.outFields = ["*"];
        query.returnGeometry = true;

      alert ( 'inside the JSAPI loaded')
     
    })
    .catch(err => {
      // handle any errors
      console.error(err);
    });
}
