import React, { Component } from 'react'
import {connect} from 'react-redux';
import {bindActionCreators} from 'redux';
import {actions as mapActions} from '../../redux/reducers/map';
import {actions as graphicActions} from '../../redux/reducers/graphic'
import Script from 'react-load-script'

const containerID = "StreetSmart-container";

class StreetSmart extends Component {

    componentDidMount() {

        this.startup( containerID);
    }

    shouldComponentUpdate(nextProps, nextState) {
        // Tell React to never update this component, that's up to us
        return false;
    }
    startup = (divId) =>{
        StreetSmartApi.init({
            username: "signworks",
            password: "SIGNWORKS",
            apiKey: "CnkxOTY52fExizg9C_EVanMh2j0RK3gxuzURif89eLsZu3ghqTAt6LEdKng56fo1",
            targetElement: document.getElementById("ssPane"),
            srs: "EPSG:2248",
            locale: 'en-us',
            configurationUrl: 'https://atlas.cyclomedia.com/configuration',
            addressSettings: {
                locale: "en",
                database: "Nokia"
            }
        }).then(
            function () {
                var viewerType = StreetSmartApi.ViewerType.PANORAMA

                StreetSmartApi.open(tx + "," + ty, {
                    viewerType: viewerType,
                    srs: 'EPSG:2248',
                }).then(
                    function (result) {
                        if (result) {
                            for (let i = 0; i < result.length; i++) {
                                if (result[i].getType() === StreetSmartApi.ViewerType.PANORAMA) {

                                    window.panoramaViewer = result[i];
                                }

                                if (viewMode == "new") {
                                    StreetSmartApi.startMeasurementMode(panoramaViewer, {
                                        geometry: StreetSmartApi.MeasurementGeometryType.POINT
                                    });
                                    let msEvents = StreetSmartApi.Events.measurement;
                                    StreetSmartApi.on(msEvents.MEASUREMENT_CHANGED, clkMap);

                                }
                                window.panoramaViewer.on(StreetSmartApi.Events.panoramaViewer.VIEW_CHANGE, changeView);
                                window.panoramaViewer.on(StreetSmartApi.Events.panoramaViewer.VIEW_LOAD_END, loadViewEnd);

                                for (o in options) {
                                    StreetSmartApi.addOverlay(options[o])
                                }




                            }

                        }
                    }.bind(this)
                ).catch(
                    function (reason) {
                        alert('Failed to create component(s) through API: ' + reason)
                    }
                );
            },
            function (err) {
                alert('Api Init Failed!' + err);
            }
        );
      
    }
    
  render() {
    return (
      <div>
    
{this.props.graphic.ssInputGeom[0].x}
<div  ref = "ssDiv" id={containerID} ></div>

    <Script src="https://cdnjs.cloudflare.com/ajax/libs/ol3/4.1.0/ol.js"></Script>
    <Script src="https://streetsmart.cyclomedia.com/api/v19.40/StreetSmartApi.js"></Script>
      </div>
    )
  }
}
const mapStateToProps = state => ({map: state.map, graphic: state.graphic, config: state.config});

const mapDispatchToProps = function (dispatch) {
    return bindActionCreators({
        ...mapActions,
        ...graphicActions
    }, dispatch);
}

export default connect(mapStateToProps, mapDispatchToProps)(StreetSmart);
