import React, {Component} from 'react'
import './SupportEditor.css'
import ModalWrapper from '../Modals/ModalWrapper';
import {SupportType, addOptionsToSelect} from '../../../SignworksJSON';

export default class SupportEditor extends Component {

    constructor(props) {
        super(props)

        this.state = {

            ...this.props.support.attributes

        }
    }

    saveClickHandler = () => {
        this
            .props
            .modalClicked(false, null)

        this
            .props
            .saveSupport(this.state.editedSupport, this.props.config.featureURLs)

    }

    cancelClickHandler = () => {
        this
            .props
            .modalClicked(false, null)
    }

    supportTypeChangeHandler = (evt) => {
       this.setState({"SUPPORTTYPE":evt.target.value})
    }

    supportTypes = new SupportType();

    render() {

        return (

            <ModalWrapper
                {...this.props}
                title="Edit Support"
                width
                ={400}
                showOk={this.props.showOk}>
                <div className="SupportEditor">
                    <p>
                        I am the Editor Pane.
                    </p>
                    <p>{this.state.OBJECTID}</p>
                    <p>Support Type:<select
                        value={this.state.SUPPORTTYPE}
                        onChange={this.supportTypeChangeHandler}>{addOptionsToSelect(this.supportTypes._codedValuesSupportType)}</select>
                    </p>
                    <p>Support Status:
                        <select value={this.state.SUPPORTSTATUS}>{addOptionsToSelect(this.supportTypes._codedValuesSupportStatus)}</select>
                    </p>

                    <button onClick={this.cancelClickHandler}>CANCEL</button>
                    <button onClick={this.saveClickHandler}>SAVE</button>

                </div>
            </ModalWrapper>
        )
    }
}
