// ACTION TYPES //
export const types = {
  MAP_LOADED: "MAP_LOADED",
  SET_FEATURES: "SET_FEATURES",
  MAP_CLICKED: "MAP_CLICKED",
  SET_SELECTED_SUPPORT:"SET_SELECTED_SUPPORT"
};

// REDUCERS //
export const initialState = {
  loaded: false,
  features: [],
  selSupport:null
};

export default (state = initialState, action) => {
  switch (action.type) {
    case types.MAP_LOADED:
      return {
        ...state,
        loaded: true
      };
    case types.SET_FEATURES:
      return {
        ...state,
        features: action.payload.features
      };
    case types.SET_SELECTED_SUPPORT:
    
    return{
      ...state,
      selSupport: action.payload.feature
    }
     
        
    default:
      return state;
  }
};

// ACTIONS //
export const actions = {
  mapLoaded: () => ({
    type: types.MAP_LOADED,
    payload: {}
  }),
  mapClicked:(geom, layer) => ({
    type:types.MAP_CLICKED,
    payload:{
      geom:geom,
      layer:layer
    }

  })
};
