// Copyright 2019 Esri Licensed under the Apache License, Version 2.0 (the
// "License"); you may not use this file except in compliance with the License.
// You may obtain a copy of the License at
// http://www.apache.org/licenses/LICENSE-2.0 Unless required by applicable law
// or agreed to in writing, software distributed under the License is
// distributed on an "AS IS" BASIS, WITHOUT WARRANTIES OR CONDITIONS OF ANY
// KIND, either express or implied. See the License for the specific language
// governing permissions and limitations under the License.​ React
import React, {Component} from 'react';

// Redux
import {bindActionCreators} from 'redux';
import {connect} from 'react-redux';
import {actions as mapActions} from '../redux/reducers/map';
import {actions as authActions} from '../redux/reducers/auth';

// Components
import TopNav from 'calcite-react/TopNav';
import TopNavTitle from 'calcite-react/TopNav/TopNavTitle';
import TopNavList from 'calcite-react/TopNav/TopNavList';
import TopNavLink from 'calcite-react/TopNav/TopNavLink';
import SceneViewExample from './esri/map/SceneViewExample';
import LoadScreen from './LoadScreen';
import UserAccount from './UserAccount';
import RightBar from "./myModules/RightBar";
import Modal from 'react-responsive-modal';

// Styled Components
import styled from 'styled-components';

const Container = styled.div `
  display: flex;
  flex-direction: column;
  position: absolute;
  width: 100%;
  height: 100%;
  text-align: center;
`;

const MapWrapper = styled.div `
  display: flex;
  flex: 1;
  flex-direction: row;
  position: relative;
  z-index: 0;
  

`;

const Nav = styled(TopNav)`
  background-color: ${props => props.theme.palette.gray};
  z-index: 5
`;

const NavList = styled(TopNavList)`
  text-align: left;
`;

// Class
class Main extends Component {

    onOpenModal = () => {
        alert('yeah baby')
        this
            .props
            .modalClicked(true);
    }
    onCloseModal = () => {
        this
            .props
            .modalClicked(false);
    }
    signIn = () => {
        this
            .props
            .checkAuth('https://www.arcgis.com');
    }

    signOut = () => {
        this
            .props
            .logout();
    }

    render() {

        return (
            <Container>
                <LoadScreen isLoading={this.props.mapLoaded}/>

                <Nav>

                    <TopNavTitle href="#">ArcGIS JS API + React Boot</TopNavTitle>
                    <NavList>
                        <TopNavLink href="https://github.com/Esri/esri-react-boot">Github</TopNavLink>
                        <TopNavLink href="https://github.com/Esri/esri-react-boot/wiki">Docs</TopNavLink>
                        <TopNavLink href="https://calcite-react.netlify.com/">Calcite-React</TopNavLink>
                    </NavList>
                    <UserAccount
                        user={this.props.auth.user}
                        portal={this.props.auth.user
                        ? this.props.auth.user.portal
                        : null}
                        loggedIn={this.props.auth.loggedIn}
                        signIn={this.signIn}
                        signOut={this.signOut}/>

                </Nav>

                <MapWrapper>
                    <SceneViewExample
                        onMapClicked={this.props.mapClicked}
                        onMapLoaded={this.props.mapLoaded}
                        mapConfig={this.props.config.mapConfig}
                        is3DScene={false}
                        />
                        <button>Ima button</button>
                    <RightBar/>
                    <Modal open={this.props.map.showModal} onClose={this.onCloseModal}>Show me some modal</Modal>
                </MapWrapper>

            </Container>
        )
    }
}

const mapStateToProps = state => ({map: state.map, auth: state.auth, config: state.config})

const mapDispatchToProps = function (dispatch) {
    return bindActionCreators({
        ...mapActions,
        ...authActions
    }, dispatch);
}

export default connect(mapStateToProps, mapDispatchToProps)(Main)
