// React
import React, { Component } from 'react';

// Redux
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import { actions as mapActions } from '../redux/reducers/map';
import { actions as authActions } from '../redux/reducers/auth';


// Components
import TopNav from 'calcite-react/TopNav';

import TopNavTitle from 'calcite-react/TopNav/TopNavTitle';
import TopNavList from 'calcite-react/TopNav/TopNavList';
import TopNavLink from 'calcite-react/TopNav/TopNavLink';
import MapView from './esri/map/MapView';
import LoadScreen from './LoadScreen';
import UserAccount from './UserAccount';

import RightBar from "./myModules/RightBar";
import logo from "../img/logo.png"


// Styled Components
import styled from 'styled-components';
import ModalConductor from './myModules/Modals/ModalConductor';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  position: absolute;
  width: 100%;
  height: 100%;
  text-align: center;
`;

const MapWrapper = styled.div`
  display: flex;
  flex: 1;
  flex-direction: row;
  position: relative;
  z-index: 0;
  

`;



const Nav = styled(TopNav)`
  background-color: ${props => props.theme.palette.gray};
  z-index: 5
`;

const NavList = styled(TopNavList)`
  text-align: left;
`;

// Class
class Main extends Component {
  signIn = () => {
    this.props.checkAuth('https://www.arcgis.com');
  }

  signOut = () => {
    this.props.logout();
  }
 
  render() {
 
    return (
      <Container>
        <LoadScreen isLoading={this.props.mapLoaded} />

        <div>
         <img src={logo}/>
         
          <UserAccount
            user={this.props.auth.user}
            portal={this.props.auth.user ? this.props.auth.user.portal : null}
            loggedIn={this.props.auth.loggedIn}
            signIn={this.signIn}
            signOut={this.signOut}
          />
          
        </div>

        <MapWrapper>
          <MapView
          onMapClicked = {this.props.mapClicked}
            onMapLoaded={this.props.mapLoaded}
            mapConfig={this.props.config.mapConfig}
            is3DScene={false}
          />
           <RightBar/>
           <ModalConductor {...this.props.map}  />
        </MapWrapper>
       
      </Container>
    )
  }
}

const mapStateToProps = state => ({
  map: state.map,
  auth: state.auth,
  config: state.config,
})

const mapDispatchToProps = function (dispatch) {
  return bindActionCreators({
    ...mapActions,
    ...authActions,
  }, dispatch);
}

export default connect(mapStateToProps, mapDispatchToProps)(Main)
