import { loadModules } from 'esri-loader';



const err = () =>{ alert ('an error occurred in JSAPI')}

const getSupportLayer = () => {
    loadModules([   'esri/layers/FeatureLayer'])
    .then(([ FeatureLayer]) => {

     return  new FeatureLayer({
        url: "https://dcdot.esriemcs.com/server/rest/services/Signs/SignWorks_Test/FeatureServer/0", outFields: ["*"]
      });
     
    })
    .catch(err => {
      // handle any errors
      console.error(err);
    });
}


export function  getSupportById (id, layer) {
   layer = getSupportLayer() ;
   id = 28216;
    loadModules([        "esri/tasks/support/Query"])
    .then(([Query]) => {
        let query = new Query();
        query.where = "OBJECTID = " + id;
        query.outFields = ["*"];
        query.returnGeometry = true;
        alert ( 'inside the JSAPI loaded')
        layer.queryFeatures(query).then(
            function (results) {
                alert('alleged results')
                if (results.features.length > 0) {
                   return results.features[0];
                } else {
                    alert('support was not retrieved')
                }
            },
            err);

     
     
    })
    .catch(err => {
      // handle any errors
      console.error(err);
    });
}
