import React, {Component} from 'react'
import {connect} from 'react-redux';
import {bindActionCreators} from 'redux';
import {actions as mapActions} from '../../redux/reducers/map';
import {actions as graphicActions} from '../../redux/reducers/graphic'
import {mapModes} from '../../redux/reducers/graphic'
import './Banner.css'
import UserAccount from '../UserAccount';
import logo from "../../img/logo.png"

class Banner extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isAdd: false,
            caption: "Add Support"
        };

    }

    bannerToolHandler = (evt) => {
        console.log('clicked', this.state.isAdd, this.state.caption);
        switch (evt.target.value) {
            case '0':
                this
                    .props
                    .setMapClickMode(mapModes.SELECT_SUPPORT_MODE, 'default');
                this.setState({isAdd: false});
                break;
            case "1": //SuperQuery™

                this
                    .props
                    .modalClicked(true, "QUERY", null);
                evt.target.value = '0'
                this.setState({isAdd: false});
                break;
            case "2":
               
                if (this.state.isAdd) {
                    alert('we are expecting to select now')
                    this.setState({caption: "Add Support", isAdd: true})
                    this
                        .props
                        .setMapClickMode(mapModes.SELECT_SUPPORT_MODE, 'default');
                } else {
                    alert('we are expecting to add now')
                    this.setState({caption: "Select Support", isAdd: false})
                    this
                        .props
                        .setMapClickMode(mapModes.ADD_SUPPORT_MODE, 'crosshair');

                }

                break;
            default:
                //do nothing

                return null;
        }
    }

    bannerActionHandler = (evt) => {
        switch (evt.target.value) {
            case '1':
                this
                    .props
                    .removeQueryGraphics();
                evt.target.value = "0"
                break;
            default:
                return null;
        }
    }

    render() {
        return (
            <div className="Banner">
                <img src={logo} className="AppLogo" alt="logo"/>

                <div className="AppTools">
                    <span>
                        <select onChange={this.bannerToolHandler} className="ActionSelect">
                            <option value={0}>...Tools</option>
                            <option value={1}>Sign Query</option>
                            <option value={2}>{this.state.caption}
                            </option>
                        </select>
                    </span>
                    <span>
                        <select onChange={this.bannerActionHandler} className="ActionSelect">
                            <option value={0}>...Actions</option>
                            <option value={1}>Clear Query Graphics</option>
                        </select>
                    </span>

                </div>

                <UserAccount
                    className="UserAccount"
                    user={this.props.auth.user}
                    portal={this.props.auth.user
                    ? this.props.auth.user.portal
                    : null}
                    loggedIn={this.props.auth.loggedIn}
                    signIn={this.signIn}
                    signOut={this.signOut}/>

            </div>
        )
    }
}

const mapStateToProps = state => ({map: state.map, graphic: state.graphic, config: state.config});

const mapDispatchToProps = function (dispatch) {
    return bindActionCreators({
        ...mapActions,
        ...graphicActions
    }, dispatch);
}

export default connect(mapStateToProps, mapDispatchToProps)(Banner);
